mapper.select:
SELECT t4.task_name as taskName, t4.type, t4.id as taskId, t4.apply_user as applyUser, t5.rule_id as ruleId, t6.name as ruleName, t7.name as accountName, t6.suggestion, t.* FROM resource_item t JOIN resource t1 ON t1.id = t.resource_id JOIN cloud_task_item_resource t2 ON t1.id = t2.resource_id and t2.resource_type in
#{item}
JOIN cloud_task_item t3 ON t3.id = t2.task_item_id JOIN cloud_task t4 ON t3.task_id = t4.id JOIN resource_rule t5 ON t1.id = t5.resource_id JOIN rule t6 ON t5.rule_id = t6.id JOIN cloud_account t7 ON t.account_id = t7.id
AND t4.id = #{taskId}
AND t.account_id = #{accountId}
AND t.region_id = #{regionId}
AND t4.task_name like CONCAT('%',#{taskName},'%')
AND t.severity = #{severity}
AND t5.rule_id in (select tt.rule_id from rule_tag_mapping tt where tt.tag_key = #{tagKey})
AND t.plugin_name = #{pluginName}
AND t.resource_name like CONCAT('%',#{resourceName},'%')
AND t.resource_status = #{resourceStatus}
AND t.resource_type = #{resourceType}
AND t5.rule_id = #{ruleId}
AND t6.name like CONCAT('%',#{ruleName},'%')
AND t.hummer_id like CONCAT('%',#{name},'%')
ORDER BY t.create_time DESC
