mapper.select:
SELECT t.*
FROM cloud_task t
WHERE t.account_id = #{id}

SELECT t.*
FROM resource t
WHERE t.id = #{resource_id}

SELECT t4.task_name as taskName, t4.type, t4.id as taskId, t4.apply_user as applyUser, t5.rule_id as ruleId, t6.name as ruleName, t7.name as accountName, t6.suggestion, t.* FROM resource_item t JOIN resource t1 ON t1.id = t.resource_id JOIN cloud_task_item_resource t2 ON t1.id = t2.resource_id JOIN cloud_task_item t3 ON t3.id = t2.task_item_id JOIN cloud_task t4 ON t3.task_id = t4.id JOIN resource_rule t5 ON t1.id = t5.resource_id JOIN rule t6 ON t5.rule_id = t6.id JOIN cloud_account t7 ON t.account_id = t7.id
AND t4.id = #{taskId}
AND t.account_id = #{accountId}
AND t.region_id = #{regionId}
AND t4.task_name like CONCAT('%',#{taskName},'%')
AND t.severity = #{severity}
AND t5.rule_id in (select tt.rule_id from rule_tag_mapping tt where tt.tag_key = #{tagKey})
AND t.plugin_name = #{pluginName}
AND t.resource_name like CONCAT('%',#{resourceName},'%')
AND t.resource_status = #{resourceStatus}
AND t.resource_type = #{resourceType}
AND t5.rule_id = #{ruleId}
AND t6.name like CONCAT('%',#{ruleName},'%')
AND t.hummer_id like CONCAT('%',#{name},'%')
ORDER BY t.create_time DESC

select IFNULL(sum( t.return_sum ), 0)/IFNULL(sum( t.resources_sum ), 1)
from cloud_task t
join cloud_account t1 on t.account_id = t1.id


        AND t.severity = #{severity}


        AND t.account_id = #{accountId}


        AND t.id = #{taskId}

select IFNULL(sum( t.return_sum ), 0)/IFNULL(sum( t.resources_sum ), 1)
from cloud_task t
join cloud_account t1 on t.account_id = t1.id


        AND t.severity = #{severity}


        AND t.account_id = #{accountId}


        AND t.id = #{taskId}

select IFNULL(sum( t.return_sum ), 0)
from history_scan_task t


        AND t.scan_id = #{id}

select IFNULL(sum( t.resources_sum ), 0)
from history_scan_task t


        AND t.scan_id = #{id}

select IFNULL(ROUND(AVG( t.scan_score ), 0), 0)
from history_scan_task t


        AND t.scan_id = #{id}

SELECT DISTINCT t.hummer_id as hummerId, t.region_id as regionId, t.region_name as regionName, t1.resource_name as resourceName, t.resource_type as resourceType, t.severity, t3.name as ruleName, t3.description as ruleDescription, IFNULL(t5.project, '暂无') as project, IFNULL(t5.item_sort_first_level, '暂无') as firstLevel, IFNULL(t5.item_sort_second_level, '暂无') as secondLevel, IFNULL(t5.improvement, '暂无') as improvement FROM resource_item t JOIN resource t1 on t.resource_id = t1.id JOIN resource_rule t2 on t.resource_id = t2.resource_id JOIN rule t3 on t2.rule_id = t3.id LEFT JOIN rule_inspection_report_mapping t4 on t3.id = t4.rule_id LEFT JOIN rule_inspection_report t5 on t4.report_id = t5.id WHERE t.account_id in
#{accountId}
ORDER BY t.resource_type, t1.resource_name, t.region_id, t.hummer_id

SELECT t.*
FROM resource t
left join cloud_task_item_resource t1 on t.id = t1.resource_id
left join cloud_task_item t2 on t1.task_item_id = t2.id


        AND t1.task_id = #{taskId}


        AND t1.task_item_id = #{id}


        AND t.account_id = #{accountId}


        AND t.region_id = #{regionId}


        AND t2.rule_id = #{ruleId}


ORDER BY t.create_time desc
limit 1

SELECT t.region_id as id, t.region_name as name, sum(IFNULL(t.return_sum, 0)) as sum FROM resource t WHERE t.account_id = #{id}
AND LOWER(t.region_name) like CONCAT('%',#{name},'%')
GROUP BY t.region_id,t.region_name ORDER BY sum desc

SELECT t.severity as id, t.severity as name, sum(IFNULL(t.return_sum, 0)) as sum FROM resource t WHERE t.account_id = #{id}
AND LOWER(t.severity) like CONCAT('%',#{name},'%')
GROUP BY t.severity ORDER BY sum desc

SELECT t.resource_type as id, t.resource_type as name, sum(IFNULL(t.return_sum, 0)) as sum FROM resource t WHERE t.account_id = #{id}
AND LOWER(t.resource_type) like CONCAT('%',#{name},'%')
GROUP BY t.resource_type ORDER BY sum desc

SELECT t.task_id as id, t2.task_name as name, sum(IFNULL(t1.return_sum, 0)) as sum FROM cloud_task_item_resource t JOIN resource t1 ON t.resource_id = t1.id JOIN cloud_task t2 ON t.task_id = t2.id WHERE t2.account_id = #{id}
AND LOWER(t2.task_name) like CONCAT('%',#{name},'%')
GROUP BY t.task_id ORDER BY sum desc

select t.*, (select count(g.id) from rule_group_mapping g where g.group_id = t.id) as ruleSum, ( select count(g.id) from rule_group_mapping g where g.group_id = t.id and g.rule_id in (select tt.rule_id from cloud_task tt where tt.resources_sum < 0 and tt.account_id = #{request.accountId}) ) as riskRuleSum, p.name as pluginName, p.icon as pluginIcon, CASE WHEN ( SELECT count(t5.id) FROM rule_group_mapping t1 JOIN rule t2 ON t1.rule_id = t2.id JOIN resource_rule t3 ON t2.id = t3.rule_id JOIN resource t4 ON t4.id = t3.resource_id JOIN resource_item t5 ON t4.id = t5.resource_id WHERE t1.group_id = t.id AND t4.account_id = c.id ) > 0 THEN 'risky' ELSE 'risk_free' END AS status, (select create_time from cloud_task where account_id = c.id order by create_time desc limit 1) as createTime, '暂无合规报告' as state, ( SELECT IFNULL(count( DISTINCT g.id ), 0) FROM rule_inspection_report r JOIN rule_inspection_report_mapping t1 ON t1.report_id = r.id JOIN cloud_task_item t2 ON t2.rule_id = t1.rule_id JOIN cloud_task t3 ON t3.id = t2.task_id JOIN rule_group_mapping t4 ON t3.rule_id = t4.rule_id JOIN rule_group g ON t4.group_id = g.id WHERE t3.account_id = c.id and g.id = t.id AND t3.return_sum > 0 ) AS riskyRegulation, ( SELECT count( DISTINCT r.id ) FROM rule_inspection_report r ) AS totalRegulation, ( SELECT IFNULL(sum( r.return_sum ), 0) FROM cloud_task r WHERE r.account_id = c.id AND r.rule_id IN (SELECT rule_id FROM rule_group_mapping WHERE group_id = t.id) ) AS returnSum, ( SELECT IFNULL(sum( r.resources_sum ), 0) FROM cloud_task r WHERE r.account_id = c.id AND r.rule_id IN (SELECT rule_id FROM rule_group_mapping WHERE group_id = t.id) ) AS resourcesSum from rule_group as t join plugin p on t.plugin_id = p.id join cloud_account c on t.plugin_id = c.plugin_id
and c.id = #{request.accountId}
and t.name like concat('%', #{request.name}, '%')
and t.plugin_id = #{request.pluginId}
order by t.plugin_id, t.flag, t.id
