mapper.select:
SELECT t.*, t3.tag_name AS tagName, IFNULL(( SELECT sum( t1.return_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 0) as ruSum, IFNULL(( SELECT sum( t1.resources_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 0) as reSum, IFNULL(concat(truncate('100%'- IFNULL( (SELECT sum( t1.return_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 0) / IFNULL(( SELECT sum( t1.resources_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 1) *100, 2),'%'), '100.00%') AS ratio FROM rule t LEFT JOIN rule_tag_mapping t2 ON t.id = t2.rule_id LEFT JOIN rule_tag t3 ON t2.tag_key = t3.tag_key LEFT JOIN cloud_account t4 ON t.plugin_id = t4.plugin_id LEFT JOIN plugin p on t.plugin_id = p.id
AND t4.`status` = 'VALID'
AND t.severity = #{severity}
AND t2.tag_key = #{tagKey}
AND t4.id = #{accountId}
and p.type = 'cloud' or p.id = 'hummer-k8s-plugin'
ORDER BY CONVERT(ratio, signed), ruSum desc

SELECT t.*, t3.tag_name AS tagName, IFNULL(( SELECT sum( t1.return_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 0) as ruSum, IFNULL(( SELECT sum( t1.resources_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 0) as reSum, IFNULL(concat(truncate('100%'- IFNULL( (SELECT sum( t1.return_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 0) / IFNULL(( SELECT sum( t1.resources_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 1) *100, 2),'%'), '100.00%') AS ratio FROM rule t LEFT JOIN rule_tag_mapping t2 ON t.id = t2.rule_id LEFT JOIN rule_tag t3 ON t2.tag_key = t3.tag_key LEFT JOIN cloud_account t4 ON t.plugin_id = t4.plugin_id LEFT JOIN plugin p on t.plugin_id = p.id
t.id in (select rule_id from resource_rule) AND t4.`status` = 'VALID'
AND t.severity = #{severity}
AND t2.tag_key = #{tagKey}
AND t4.id = #{accountId}
and p.type = 'vuln'
ORDER BY CONVERT(ratio, signed), ruSum desc

SELECT IFNULL( sum(t2.return_sum), 0 ) AS yAxis, IFNULL( sum(t2.resources_sum), 0 ) AS yAxis2, t.region_name AS groupName, t.region_id AS xAxis FROM cloud_task_item t LEFT JOIN cloud_task_item_resource t1 ON t.id = t1.task_item_id LEFT JOIN resource t2 ON t2.id = t1.resource_id LEFT JOIN plugin p on t2.plugin_id = p.id
AND t.account_id = #{accountId}
AND p.type = 'cloud'
GROUP BY t.region_id,t.region_name HAVING yAxis != 0 ORDER BY yAxis desc
LIMIT #{limit}

SELECT IFNULL( t.return_sum, 0 ) AS yAxis, IFNULL( t.resources_sum, 0 ) AS yAxis2, t.task_name AS groupName, t.id AS xAxis FROM cloud_task t LEFT JOIN plugin p on t.plugin_id = p.id
AND t.account_id = #{accountId}
AND p.type = 'vuln'
GROUP BY t.id HAVING yAxis != 0 ORDER BY yAxis desc
LIMIT #{limit}

SELECT t.id, t.account_id as accountId, t.create_time as createTime, t.`operator`, t.resources_sum as resourcesSum, t.return_sum as returnSum, t.scan_score as scanScore, t.output, t1.name as accountName, t1.plugin_id as pluginId, t1.plugin_name as pluginName, t1.plugin_icon as pluginIcon FROM history_scan t LEFT JOIN cloud_account t1 ON t.account_id = t1.id LEFT JOIN plugin p on t1.plugin_id = p.id
AND t.account_id = #{accountId}
AND t1.plugin_id = #{pluginId}
AND p.type = 'cloud'
ORDER BY t.create_time DESC

SELECT t.id, t.account_id as accountId, t.create_time as createTime, t.`operator`, t.resources_sum as resourcesSum, t.return_sum as returnSum, t.scan_score as scanScore, t.output, t1.name as accountName, t1.plugin_id as pluginId, t1.plugin_name as pluginName, t1.plugin_icon as pluginIcon FROM history_scan t LEFT JOIN cloud_account t1 ON t.account_id = t1.id LEFT JOIN plugin p on t1.plugin_id = p.id
AND t.account_id = #{accountId}
AND t1.plugin_id = #{pluginId}
AND p.type = 'vuln'
ORDER BY t.create_time DESC

SELECT t.*, IFNULL( sum( t4.return_sum ), 0 ) AS returnSum, IFNULL( sum( t4.resources_sum ), 0 ) AS resourcesSum, CASE WHEN ( SELECT count(t5.id) FROM rule_inspection_report_mapping t1 JOIN rule t2 ON t1.rule_id = t2.id JOIN resource_rule t3 ON t2.id = t3.rule_id JOIN resource t4 ON t3.resource_id = t4.id JOIN resource_item t5 ON t4.id = t5.resource_id JOIN plugin p on t2.plugin_id = p.id WHERE t1.report_id = t.id AND p.type = 'vuln'
AND t4.account_id = #{accountId}
) > 0 THEN 'risky' ELSE 'risk_free' END AS status FROM rule_inspection_report t LEFT JOIN rule_inspection_report_mapping t1 ON t.id = t1.report_id LEFT JOIN rule t2 ON t1.rule_id = t2.id LEFT JOIN resource_rule t3 ON t2.id = t3.rule_id LEFT JOIN resource t4 ON t3.resource_id = t4.id LEFT JOIN plugin p on t2.plugin_id = p.id
AND t4.account_id = #{accountId}
AND p.type = 'vuln'
GROUP BY t.id ORDER BY returnSum DESC

SELECT t5.resource_name as resourceName, t5.resource_status as resourceStatus, t5.resource_type as resourceType, t5.return_sum as returnSum, t5.resources_sum as resourcesSum, t5.plugin_name as pluginName, t5.plugin_icon as pluginIcon, t5.region_name as regionName, t5.severity as severity, t5.update_time as updateTime, t5.return_html as returnHtml, t5.resources as resources, t5.id as id FROM resource t5 JOIN plugin p on t5.plugin_id = p.id
AND t5.resource_name = #{resourceName}
AND t5.resource_type = #{resourceType}
AND t5.plugin_name = #{pluginName}
AND p.type = 'vuln'
ORDER BY t5.create_time DESC
