mapper.select:
SELECT DISTINCT * FROM ( SELECT `user`.* FROM user_role JOIN `user` ON user_role.user_id = `user`.id WHERE user_role.source_id = #{member.workspaceId}
AND `user`.name like CONCAT('%', #{member.name},'%')
order by user_role.update_time desc) temp

SELECT DISTINCT * FROM ( SELECT `user`.* FROM user_role JOIN `user` ON user_role.user_id = `user`.id WHERE user_role.source_id = #{orgMember.organizationId}
AND `user`.name like CONCAT('%', #{orgMember.name},'%')
order by user_role.update_time desc) temp
