mapper.select:
SELECT t.*, t3.tag_name AS tagName, IFNULL(( SELECT sum( t1.return_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 0) as ruSum, IFNULL(( SELECT sum( t1.resources_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 0) as reSum, IFNULL(concat(truncate('100%'- IFNULL( (SELECT sum( t1.return_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 0) / IFNULL(( SELECT sum( t1.resources_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id ), 1) *100, 2),'%'), '100.00%') AS ratio FROM rule t LEFT JOIN rule_tag_mapping t2 ON t.id = t2.rule_id LEFT JOIN rule_tag t3 ON t2.tag_key = t3.tag_key LEFT JOIN cloud_account t4 ON t.plugin_id = t4.plugin_id
t.id in (select rule_id from resource_rule) AND t4.`status` = 'VALID'
AND t.severity = #{severity}
AND t2.tag_key = #{tagKey}
AND t4.id = #{accountId}
AND (SELECT SUM( t1.return_sum ) FROM resource t1 LEFT JOIN resource_rule t2 ON t1.id = t2.resource_id WHERE t2.rule_id = t.id) != 0
ORDER BY CONVERT(ratio, signed), ruSum desc

SELECT IFNULL( sum(t2.return_sum), 0 ) AS yAxis, IFNULL( sum(t2.resources_sum), 0 ) AS yAxis2, t.region_name AS groupName, t.region_id AS xAxis FROM task_item t LEFT JOIN task_item_resource t1 ON t.id = t1.task_item_id LEFT JOIN resource t2 ON t2.id = t1.resource_id
AND t.account_id = #{accountId}
GROUP BY t.region_id,t.region_name HAVING yAxis != 0 ORDER BY yAxis desc
LIMIT #{limit}

SELECT t.id, t.account_id as accountId, t.create_time as createTime, t.`operator`, t.resources_sum as resourcesSum, t.return_sum as returnSum, t.scan_score as scanScore, t.output, t1.name as accountName, t1.plugin_id as pluginId, t1.plugin_name as pluginName, t1.plugin_icon as pluginIcon FROM scan_history t LEFT JOIN cloud_account t1 ON t.account_id = t1.id
AND t.account_id = #{accountId}
AND t1.plugin_id = #{pluginId}
AND t1.plugin_id not in ('hummer-nuclei-plugin', 'hummer-xray-plugin')
ORDER BY t.create_time desc
