INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('1240a2b8-f7ef-47c3-8048-2dd95d6d5b54', 'Tencent EIP带宽峰值检测', 1, 'HighRisk', 'Tencent  检测您账号下的弹性IP实例是否达到最低带宽要求，是视为“合规”，否则视为“不合规”', 'policies:\n    # 检测您账号下的弹性IP实例是否达到最低带宽要求\n    - name: tencent-eip-bandwidth\n      resource: tencent.eip\n      filters:\n        - type: bandwidth\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"按带宽计费的公网型实例的带宽峰值\",\"defaultValue\":\"10\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('159e19e9-e956-4c52-92fc-c9746372e448', 'Tencent CVM实例关机计费模式检测', 1, 'HighRisk', 'Tencent CVM实例的关机计费模式是否为关机停止收费，是视为“合规”，否则视为“不合规”', 'policies:\n    # CVM实例的关机计费模式是否为关机停止收费，是视为“合规”，否则视为“不合规”\n    - name: tencent-cvm-stop-charging-mode\n      resource: tencent.cvm\n      filters:\n        - type: stop-charging-mode\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"计费模式\",\"defaultValue\":\"STOP_CHARGING\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('17dbe1a1-e9e7-4513-aa07-597ae932ff73', 'Tencent CVM实例公网IP检测', 1, 'HighRisk', 'Tencent CVM实例未直接绑定公网IP，视为“合规”，否则属于“不合规”。该规则仅适用于 IPv4 协议', 'policies:\n    # CVM实例未直接绑定公网IP，视为“合规”。该规则仅适用于 IPv4 协议\n    - name: tencent-cvm-public-ip-address\n      resource: tencent.cvm\n      filters:\n        - type: public-ip-address', '[]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('21fc2518-3b2b-475a-8398-4b1d9f68cfdb', 'Tencent Redis实例网络类型检测', 1, 'HighRisk', 'Tencent  检测您账号下的Redis实例是否运行在VPC网络环境下，是视为“合规”，否则属于“不合规”', 'policies:\n    # 检测您账号下的Redis实例是否运行在VPC网络环境下，是视为“合规”\n    - name: tencent-redis-network-type\n      resource: tencent.redis\n      filters:\n        - type: network-type\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"实例的网络类型\",\"defaultValue\":\"vpc\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('2631d8a3-e5d8-4ef3-88c9-a8176fae110b', 'Tencent SecurityGroup安全组配置检测', 1, 'HighRisk', 'Tencent  账号下CVM安全组配置不为“0.0.0.0/0”，视为“合规”，否则属于“不合规”', 'policies:\n    # 账号下ECS安全组配置不为“0.0.0.0/0”，视为“合规”。\n    - name: tencent-sg-source-cidr-ip\n      resource: tencent.security-group\n      filters:\n        - type: source-cidr-ip\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"目标IP地址段\",\"defaultValue\":\"\\\"0.0.0.0/0\\\"\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('2c4de559-7182-44e7-9657-b97412f0f0a7', 'Tencent CDB实例网络类型检测', 1, 'HighRisk', 'Tencent  账号下CDB实例已关联到VPC；若您配置阈值，则关联的VpcId需存在您列出的阈值中，视为“合规”，否则属于“不合规”', 'policies:\n    # 账号下CDB实例已关联到VPC；若您配置阈值，则关联的VpcId需存在您列出的阈值中，视为“合规”。\n    - name: tencent-cdb-instance-network-type\n      resource: tencent.cdb\n      filters:\n        - type: network-type\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"实例的网络类型\",\"defaultValue\":\"vpc\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('3e66d418-3e47-475b-a863-03a9653940ed', 'Tencent COS存储桶加密检测', 1, 'HighRisk', 'Tencent  查看并确认您的COS存储桶启用了默认加密，未开启则该存储桶不合规', 'policies:\n    # 查看并确认您的COS存储桶启用了默认加密，未开启则该存储桶不合规\n    - name: tencent-cos-encryption\n      resource: tencent.cos\n      filters:\n        - type: encryption\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"是否加密\",\"defaultValue\":\"true\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('4cffcef8-be1e-44e0-8057-892e2cf588d5', 'Tencent CDB实例高可用状态检测', 1, 'HighRisk', 'Tencent 账号下CDB实例具备高可用能力，视为“合规”，否则属于“不合规”', 'policies:\n    # 账号下CDB实例具备高可用能力，视为“合规”，否则属于“不合规”。\n    - name: tencent-cdb-device-type\n      resource: tencent.cdb\n      filters:\n        - type: device-type\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"实例类型\",\"defaultValue\":\"BASIC\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('525d0b91-ca93-4d3d-8679-195aef136e0b', 'Tencent CLB公网访问检测', 1, 'HighRisk', 'Tencent  账号下CLB不允许任意来源公网访问，视为“合规”，否则属于“不合规”', 'policies:\n    # 账号下CLB不允许任意来源公网访问，视为“合规”\n    - name: tencent-clb-acls\n      resource: tencent.clb\n      filters:\n        - type: acls\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"网络类型\",\"defaultValue\":\"INTERNAL\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('59a95d30-30d7-45bf-ac47-80afed002ade', 'Tencent CLB负载均衡实例关联到VPC检测', 1, 'HighRisk', 'Tencent  账号下负载均衡实例已关联到VPC；若您配置阈值，则关联的VpcId需存在您列出的阈值中，视为“合规”，否则视为“不合规”', 'policies:\n    # 账号下负载均衡实例已关联到VPC；若您配置阈值，则关联的VpcId需存在您列出的阈值中，视为“合规”。\n    - name: tencent-clb-network-type\n      resource: tencent.clb\n      filters:\n        - type: network-type\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"负载均衡实例的网络类型\",\"defaultValue\":\"vpc\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('68363ad8-cce8-4458-b8f1-9bd1d1af54a7', 'Tencent CDB实例多可用区检测', 1, 'HighRisk', 'Tencent 账号下CDB实例支持多可用区，视为“合规”，否则视为“不合规”', 'policies:\n    # 账号下CDB实例支持多可用区，视为“合规”。\n    - name: tencent-cdb-availablezones\n      resource: tencent.cdb\n      filters:\n        - type: availablezones\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"支持多可用区\",\"defaultValue\":\"true\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('77eab07c-1e3c-4713-acf1-ed1bede0531d', 'Tencent COS公开读取访问权限检测', 1, 'HighRisk', 'Tencent  查看您的COS存储桶是否不允许公开读取访问权限。如果某个COS存储桶策略或存储桶 ACL 允许公开读取访问权限，则该存储桶不合规', 'policies:\n    # 查看您的COS存储桶是否不允许公开读取访问权限。\n    - name: tencent-cos-global-grants\n      resource: tencent.cos\n      filters:\n        - type: global-grants\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"公开读取访问权限\",\"defaultValue\":\"READ\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('a0dca246-563a-49c3-8a43-3c537ba1fb36', 'Tencent CLB带宽峰值检测', 1, 'HighRisk', 'Tencent  检测您账号下的CLB负载均衡实例是否达到最低带宽要求，是视为“合规”，否则视为“不合规”', 'policies:\n    # 检测您账号下的负载均衡实例是否达到最低带宽要求。\n    - name: tencent-clb-bandwidth\n      resource: tencent.clb\n      filters:\n        - type: bandwidth\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"按带宽计费的公网型实例的带宽峰值\",\"defaultValue\":\"10\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('bfbf6be1-69f8-448e-9c23-c5267530d573', 'Tencent Disk磁盘加密检测', 1, 'HighRisk', 'Tencent 账号下所有的磁盘均已加密，视为“合规”，否则视为“不合规”', 'policies:\n    # 账号下所有的磁盘均已加密，视为“合规”，否则视为“不合规”\n    - name: tencent-disk-encrypt\n      resource: tencent.disk\n      filters:\n        - type: encrypt\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"磁盘是否加密( true/false )\",\"defaultValue\":\"true\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('c1b41cbc-1263-4d67-a7c6-cf064999c6f8', 'Tencent MongoDB实例网络类型检测', 1, 'HighRisk', 'Tencent  检测您账号下的MongoDB实例是否运行在VPC网络环境下，是视为“合规”，否则视为“不合规”', 'policies:\n    # 检测您账号下的MongoDB实例是否运行在VPC网络环境下\n    - name: tencent-mongodb-network-type\n      resource: tencent.mongodb\n      filters:\n        - type: network-type\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"实例的网络类型\",\"defaultValue\":\"vpc\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('c95be861-6e0e-4404-9b21-5ccfcd94ee3f', 'Tencent MongoDB实例公网访问检测', 1, 'HighRisk', 'Tencent  检测您账号下MongoDB实例不允许任意来源公网访问，视为“合规”，否则属于“不合规”', 'policies:\n    # 检测您账号下MongoDB实例不允许任意来源公网访问，视为“合规”\n    - name: tencent-mongodb-internet-access\n      resource: tencent.mongodb\n      filters:\n        - type: internet-access\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"启用公网访问\",\"defaultValue\":\"true\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('ca991f61-49a4-49b2-9d14-16ca338d8149', 'Tencent COS防盗链检测', 1, 'HighRisk', 'Tencent  检测COS Bucket是否开启防盗链开关，已开通视为“合规”，否则属于“不合规”', 'policies:\n    # 检测COS Bucket是否开启防盗链开关，已开通视为“合规”。\n    - name: tencent-cos-bucket-referer\n      resource: tencent.cos\n      filters:\n        - type: bucket-referer\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"是否开启防盗链开关\",\"defaultValue\":\"true\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('cb9320af-4ee6-4c11-8840-8ec4b9cc8276', 'Tencent CDB实例公网访问检测', 1, 'HighRisk', 'Tencent  检测您账号下CDB实例不允许任意来源公网访问，视为“合规”，否则属于“不合规”', 'policies:\n    # 检测您账号下CDB实例不允许任意来源公网访问，视为“合规”\n    - name: tencent-cdb-internet-access\n      resource: tencent.cdb\n      filters:\n        - type: internet-access\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"启用公网访问\",\"defaultValue\":\"true\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('f8badb51-4a56-48cc-993f-6eb6698ea0ea', 'Tencent COS公开写入访问权限检测', 1, 'HighRisk', 'Tencent  查看您的COS存储桶是否不允许公开写入访问权限。如果某个COS存储桶策略或存储桶 ACL 允许公开写入访问权限，则该存储桶不合规', 'policies:\n    # 查看您的COS存储桶是否不允许公开写入访问权限。\n    - name: tencent-cos-global-grants\n      resource: tencent.cos\n      filters:\n        - type: global-grants\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"公开读取访问权限\",\"defaultValue\":\"WRITE\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
INSERT INTO rule(id, name, status, severity, description, script, parameter, plugin_id, plugin_name, plugin_icon, last_modified, flag, scan_type) VALUES ('f9d3db46-d340-4487-ae9b-82b827108912', 'Tencent SecurityGroup高危安全组检测', 1, 'HighRisk', 'Tencent  检测安全组是否开启风险端口，未开启视为“合规”，否则属于“不合规”', 'policies:\n  #检测开放以下高危端口的安全组：\n  #(20,21,22,25,80,773,765,1733,1737,3306,3389,7333,5732,5500)\n  - name: tencent-security-group\n    resource: tencent.security-group\n    description: |\n      Add Filter all security groups, filter ports\n      [20,21,22,25,80,773,765,1733,1737,3306,3389,7333,5732,5500]\n      on 0.0.0.0/0 or\n      [20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\n      on ::/0 (IPv6)\n    filters:\n        - or:\n            - type: ingress\n              IpProtocol: \"-1\"\n              Ports: ${{ipv4_port}}\n              Cidr: \"0.0.0.0/0\"\n              Action: ${{action}}\n            - type: ingress\n              IpProtocol: \"-1\"\n              Ports: ${{ipv6_port}}\n              CidrV6: \"::/0\"', '[{\"key\":\"ipv4_port\",\"name\":\"ipv4端口\",\"defaultValue\":\"[20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\",\"required\":true},{\"key\":\"ipv6_port\",\"name\":\"ipv6端口\",\"defaultValue\":\"[20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\",\"required\":true}]', 'hummer-qcloud-plugin', '腾讯云', 'qcloud.png', concat(unix_timestamp(now()), '003'), 1, 'custodian');
