INSERT INTO rule_group (`name`, `description`, `level`, `plugin_id`, `flag`) VALUES ('AWS S3合规基线', 'S3 合规检查为您提供全方位的对象存储资源检查功能。', '高风险', 'hummer-aws-plugin', 1);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('07eb95ef-6d78-4b9a-8555-a42b6a16de99', 'AWS ELB SSL 黑名单检测', 1, 'HighRisk', 'AWS  测您账号下ELB SSL 黑名单检测，在白名单视为“合规”，黑名单视为“不合规”', 'policies:\n    # 测您账号下ELB SSL 黑名单检测，在白名单视为“合规”，黑名单视为“不合规”\n    - name: aws-elb-ssl-whitelist\n      resource: aws.elb\n      filters:\n        - type: ssl-policy\n          blacklist:\n            - Protocol-TLSv1\n            - Protocol-TLSv1.1\n            - Protocol-TLSv1.2', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('0be7f5d4-766c-4b2b-910d-bec0f3aac977', 'AWS ELB实例使用检测', 1, 'HighRisk', 'AWS  检测您账号下弹性负载均衡器是否使用，使用视为“合规”，否则视为“不合规”', 'policies:\n    # 检测您账号下弹性负载均衡器是否使用，使用视为“合规”，否则视为“不合规”\n    - name: aws-elb-unused\n      resource: aws.elb\n      filters:\n        - \"tag:maid_status\": absent\n        - Instances: []', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('0dda84c1-794b-4977-bb66-6f12695c6c51', 'AWS RDS实例公网访问检测', 1, 'HighRisk', 'AWS  检测您账号下RDS实例不允许任意来源公网访问，视为“合规”，否则视为“不合规”', 'policies:\n    # 检测您账号下RDS实例不允许任意来源公网访问，视为“合规”，否则视为“不合规”。\n    - name: aws-rds-publicly-accessible\n      resource: aws.rds\n      filters:\n        - PubliclyAccessible: ${{value}}', '[{\"key\":\"value\",\"name\":\"启用公网访问\",\"defaultValue\":\"true\",\"required\":true}]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('35b5c651-5bd6-44b8-85ee-ae6adfa42dc3', 'AWS EIP连接检测', 1, 'HighRisk', 'AWS  检测您账号下的弹性IP实例是否已连接，是视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的弹性IP实例是否已连接，是视为“合规”，否则视为“不合规”\n  - name: aws-unused-network-addr\n    resource: aws.network-addr\n    filters:\n      - NetworkInterfaceId: absent\n      - AssociationId: absent', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('43a1556b-5417-4efb-88fc-33e8eeb68f71', 'AWS SecurityGroup高危安全组检测', 1, 'HighRisk', 'AWS  检测安全组是否开启风险端口，未开启视为“合规”，否则属于“不合规”', 'policies:\n  #检测开放以下高危端口的安全组：\n  #(20,21,22,25,80,773,765,1733,1737,3306,3389,7333,5732,5500)\n  - name: aws-security-group\n    resource: aws.security-group\n    description: |\n      Add Filter all security groups, filter ports\n      [20,21,22,25,80,773,765,1733,1737,3306,3389,7333,5732,5500]\n      on 0.0.0.0/0 or\n      [20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\n      on ::/0 (IPv6)\n    filters:\n        - or:\n            - type: ingress\n              Ports: ${{ipv4_port}}\n              Cidr: \"0.0.0.0/0\"\n            - type: ingress\n              Ports: ${{ipv6_port}}\n              CidrV6: \"::/0\"', '[{\"key\":\"ipv4_port\",\"name\":\"ipv4端口\",\"defaultValue\":\"[20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\",\"required\":true},{\"key\":\"ipv6_port\",\"name\":\"ipv6端口\",\"defaultValue\":\"[20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\",\"required\":true}]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('5c47228c-7fe1-484b-a5b6-7c1968074f69', 'AWS EC2运行时间检测', 1, 'LowRisk', 'AWS  检测您账号下的EC2是否运行时间达到n天，没有达到视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的EC2是否运行时间达到n天，没有达到视为“合规”，否则视为“不合规”\n  - name: aws-ec2-old-instances\n    resource: aws.ec2\n    filters:\n      - "State.Name": running\n      - type: instance-age\n        days: ${{day}}', '[{\"key\":\"day\",\"name\":\"运行天数\",\"defaultValue\":\"90\",\"required\":true}]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('a6c513a8-8e18-4341-94b7-b6588fdcd1f4', 'AWS RDS实例加密检测', 1, 'HighRisk', 'AWS  检测您账号下RDS实例是否加密，加密视为“合规”，否则视为“不合规”', 'policies:\n    # 检测您账号下RDS实例是否加密，加密视为“合规”，否则视为“不合规”\n    - name: aws-rds-unencrypted\n      resource: aws.rds\n      filters:\n        - StorageEncrypted: ${{value}}', '[{\"key\":\"value\",\"name\":\"是否加密\",\"defaultValue\":\"false\",\"required\":true}]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('a90a1ba1-b392-4bf2-af31-20ecbefe5811', 'AWS EBS加密检测', 1, 'HighRisk', 'AWS  检测您账号下的EBS是否加密，加密视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的EBS是否加密，加密视为“合规”，否则视为“不合规”\n  - name: aws-unencrypted-ebs\n    resource: aws.ebs\n    filters:\n      - Encrypted: ${{value}}', '[{\"key\":\"value\",\"name\":\"是否加密\",\"defaultValue\":\"false\",\"required\":true}]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('b1491f69-f3b8-40ae-9659-4242dbc30a0b', 'AWS RDS实例是否使用检测', 1, 'HighRisk', 'AWS  检测您账号下RDS实例是否使用，使用视为“合规”，否则视为“不合规”', 'policies:\n    # 检测您账号下RDS实例是否使用，使用视为“合规”，否则视为“不合规”\n    - name: aws-rds-unused-databases\n      resource: aws.rds\n      filters:\n        - \"tag:c7n_rds_unused\": absent\n        - type: value\n          value_type: age\n          key: InstanceCreateTime\n          value: ${{day}}\n          op: greater-than', '[{\"key\":\"day\",\"name\":\"天（大于）\",\"defaultValue\":\"21\",\"required\":true}]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('b57cbdba-b4d7-4da7-84db-25b9f2d5324b', 'AWS EC2标签规范检测', 1, 'HighRisk', 'AWS  检测您账号下的EC2实例是否符合标签规范（非ASG），符合视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的EC2实例是否符合标签规范（非ASG），符合视为“合规”，否则视为“不合规”\n  - name: aws-ec2-tag\n    resource: aws.ec2\n    comment: |\n      Find all (non-ASG) instances that are not conformant\n      to tagging policies.\n    filters:\n      - \"tag:aws:autoscaling:groupName\": absent\n      - \"tag:c7n_status\": absent\n      - or:\n          - \"tag:Owner\": ${{Owner}}\n          - \"tag:CostCenter\": ${{CostCenter}}\n          - \"tag:Project\": ${{Project}}', '[{\"key\":\"Owner\",\"name\":\"tag:Owner\",\"defaultValue\":\"absent\",\"required\":true},{\"key\":\"CostCenter\",\"name\":\"tag:CostCenter\",\"defaultValue\":\"absent\",\"required\":true},{\"key\":\"Project\",\"name\":\"tag:Project\",\"defaultValue\":\"absent\",\"required\":true}]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('bd1a0479-ef54-4208-a520-50caf6acfe87', 'AWS EC2实例关机状态检测', 1, 'HighRisk', 'AWS  检测您账号下的EC2实例是关机，开机视为“合规”，关机视为“不合规”', 'policies:\n  # 检测您账号下的EC2实例是关机，开机视为“合规”，关机视为“不合规”\n  - name: aws-ec2-mark-stopped-instance\n    resource: aws.ec2\n    filters:\n      - \"tag:c7n_stopped_instance\": absent\n      - \"State.Name\": ${{state}}', '[{\"key\":\"state\",\"name\":\"实例状态\",\"defaultValue\":\"stopped\",\"required\":true}]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('befc89d7-1811-404a-9226-f8ecc22820e0', 'AWS EBS连接检测', 1, 'HighRisk', 'AWS  检测您账号下的EBS是否连接，连接视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的EBS是否连接，连接视为“合规”，否则视为“不合规”\n  - name: aws-unattached-ebs\n    resource: aws.ebs\n    filters:\n      - Attachments: []\n      - \"tag:maid_status\": absent', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('c30779c4-44b8-4c7b-b2ec-29ff3a96033b', 'AWS S3全局访问检测', 1, 'HighRisk', 'AWS  检测您账号下的S3是否允许在其ACL中进行全局访问存储桶，不允许视为“合规”，否则视为“不合规”', 'policies:\n\n    - name: s3-global-access\n      resource: aws.s3\n      filters:\n        - type: global-grants', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('d0f3f4b0-000a-4407-85ee-ed4a2f9dac44', 'AWS S3公开读写访问权限检测', 1, 'HighRisk', 'AWS  查看您的S3存储桶是否不允许公开读写访问权限。如允许则该存储桶不合规', 'policies:\n    # 查看您的S3存储桶是否不允许公开读写访问权限。如允许则该存储桶不合规\n    - name: s3-public-acls\n      resource: aws.s3\n      filters:\n        - type: check-public-block\n          BlockPublicAcls: false\n          BlockPublicPolicy: false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('fa5e89e3-417d-4296-9d17-ca51ed914be5', 'AWS EC2 CPU使用率检测', 1, 'LowRisk', 'AWS  检测您账号下的EC2是否CPU使用率在x天内低于y，高于y视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的EC2是否CPU使用率在x天内低于y，高于y视为“合规”，否则视为“不合规”\n  - name: aws-ec2-underutilized\n    resource: aws.ec2\n    filters:\n      - type: metrics\n        name: CPUUtilization\n        days: ${{day}}\n        period: 86400  # 检测周期(86400s为一天)\n        statistics: Average\n        value: ${{used}}\n        op: less-than', '[{\"key\":\"day\",\"name\":\"n天(cpu平均值小于x)\",\"defaultValue\":\"7\",\"required\":true},{\"key\":\"used\",\"name\":\"cpu平均值小于x(n天)\",\"defaultValue\":\"30\",\"required\":true}]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian');
