INSERT INTO rule_group (`name`, `description`, `level`, `plugin_id`, `flag`) VALUES ('Azure 等保预检', '等保合规检查（全称为等级保护合规检查）为您提供了全面覆盖通信网络、区域边界、计算环境和管理中心的网络安全检查。', '等保三级', 'hummer-azure-plugin', 1);
INSERT INTO rule_group (`name`, `description`, `level`, `plugin_id`, `flag`) VALUES ('Azure CIS合规检查', 'CIS（Center for Internet Security）合规检查能力，为您动态且持续地监控您保有在云上的资源是否符合 CIS Control 网络安全架构要求。', '高风险', 'hummer-azure-plugin', 1);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('29ee6650-3e4f-44e3-aadd-0ae15618afa9', 'Azure 公网IP地址DDoS攻击检测', 1, 'MediumRisk', 'Azure  检测您账号下的公网IP地址是否受到DDoS攻击，没有受到DDoS攻击视为“合规”，否则视为“不合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的公网IP地址是否受到DDoS攻击，没有受到DDoS攻击视为“合规”，否则视为“不合规”\n  - name: azure-publicip-dropping-packets\n    resource: azure.publicip\n    filters:\n      - type: metric\n        metric: IfUnderDDoSAttack\n        op: gt\n        aggregation: maximum\n        threshold: 0\n        timeframe: ${{timeframe}}', '[{\"key\":\"timeframe\",\"name\":\"小时(h)\",\"defaultValue\":\"72\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('3e324555-83e2-4ac9-8ac2-1dae350d52ff', 'Azure 网络安全组检测', 1, 'MediumRisk', 'Azure  检测您账号下的网络安全组是否打开了某些端口、关闭了某些端口，符合视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的网络安全组是否打开了某些端口、关闭了某些端口，符合视为“合规”，否则视为“不合规”\n  - name: azure-close-egress-except-TCP\n    resource: azure.networksecuritygroup\n    filters:\n     - type: ingress\n       ports: ${{allow_port}}\n       access: \'Allow\'\n     - type: ingress\n       ports: ${{deny_port}}\n       access: \'Deny\'', '[{\"key\":\"allow_port\",\"name\":\"打开了的端口\",\"defaultValue\":\"\'80\'\",\"required\":true},{\"key\":\"deny_port\",\"name\":\"关闭了的端口\",\"defaultValue\":\"\'443-3306\'\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('4852857f-b454-4375-b405-0033fed73f09', 'Azure VM虚拟机公网IP检测', 1, 'HighRisk', 'Azure  检测您账号下的VM是否开放公网IP，不开放视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的VM是否开放公网IP，不开放视为“合规”，否则视为“不合规”\n  - name: azure-vms-with-public-ip\n    resource: azure.vm\n    filters:\n     - type: network-interface\n       key: \'properties.ipConfigurations[].properties.publicIPAddress.id\'\n       value: ${{value}}', '[{\"key\":\"value\",\"name\":\"开放公网IP\",\"defaultValue\":\"not-null\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('5870d5df-7674-4f40-ae06-0408c935acd6', 'Azure SQL数据库保留策略检测', 1, 'MediumRisk', 'Azure  检测您的账户下SQL数据库短期保留至少n天，符合视为“合规”，否则视为“不合规”', 'policies:\n    # 检测您的账户下SQL数据库短期保留至少n天，符合视为“合规”，否则视为“不合规”\n    - name: azure-short-term-backup-retention-policy\n      resource: azure.sqldatabase\n      filters:\n        - type: short-term-backup-retention\n          op: lt\n          retention-period-days: ${{value}}', '[{\"key\":\"value\",\"name\":\"SQL数据库短期保留至少n天\",\"defaultValue\":\"7\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('618a61bb-eecc-4109-b387-4a313f10c1a9', 'Azure LoadBalancer负载均衡器检测', 1, 'HighRisk', 'Azure  检测您账号下的负载均衡器是否开放了公网IP，未开放视为“合规”，否则视为“不合规”', 'policies:\n   # 检测您账号下的负载均衡器是否开放了公网IP，未开放视为“合规”，否则视为“不合规”\n   - name: azure-loadbalancer-with-ipv6-frontend\n     resource: azure.loadbalancer\n     filters:\n        - type: frontend-public-ip\n          key: properties.publicIPAddressVersion\n          op: in\n          value_type: normalize\n          value: \"ipv6\"', '[]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('6873838e-3a47-4255-b49e-af5fbf5cb454', 'Azure App Services CORS配置检测', 1, 'MediumRisk', 'Azure  检测您的账户下App Services是否所有跨源资源共享（CORS）配置设置为允许所有来源的应用程序服务（Web应用程序和功能），符合视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您的账户下App Services是否所有跨源资源共享（CORS）配置设置为允许所有来源的应用程序服务（Web应用程序和功能），符合视为“合规”，否则视为“不合规”\n  - name: azure-app-service-cors-policy\n    resource: azure.webapp\n    filters:\n      - type: configuration\n        key: cors.allowedOrigins\n        value: ${{value}}\n        op: contains', '[{\"key\":\"value\",\"name\":\"允许所有来源\",\"defaultValue\":\"\'*\'\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('6967e834-ace6-48d2-9f95-f541b614810d', 'Azure VM虚拟机使用率检测', 1, 'LowRisk', 'Azure  检测您账号下的虚拟机是否x小时内CPU使用率<= y％，使用率> y％视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的虚拟机是否x小时内CPU使用率<= y％，使用率> y％视为“合规”，否则视为“不合规”\n  - name: azure-unused-vms\n    resource: azure.vm\n    filters:\n      - type: metric\n        metric: Percentage CPU\n        op: le\n        aggregation: average\n        threshold: ${{threshold}}\n        timeframe: ${{timeframe}}', '[{\"key\":\"threshold\",\"name\":\"使用率(%)\",\"defaultValue\":\"1\",\"required\":true},{\"key\":\"timeframe\",\"name\":\"小时(h)\",\"defaultValue\":\"72\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('6a71a114-ba92-4e0b-bd9d-2f6c8ecee88d', 'Azure 资源组检测', 1, 'LowRisk', 'Azure  检测您的账户下资源组是否是空的，非空视为“合规”，否则视为“不合规”', 'policies:\n    # 检测您的账户下资源组是否是空的，非空视为“合规”，否则视为“不合规”\n    - name: azure-rg-empty\n      resource: azure.resourcegroup\n      filters:\n        - type: empty-group', '[]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('77ac25d5-0a66-4890-aa7e-5b276fce299a', 'Azure 存储公网访问检测', 1, 'HighRisk', 'Azure  检测您的账户下存储是否具有指定ip规则的存储帐户的访问(公共访问)，符合视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您的账户下存储是否具有指定ip规则的存储帐户的访问，符合视为“合规”，否则视为“不合规”\n  - name: azure-storage-block-public-access\n    resource: azure.storage\n    filters:\n    - type: value\n      key: properties.networkAcls.ipRules\n      value_type: size\n      op: ne\n      value: 0', '[]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('802fbf6c-92ac-4153-bea3-42b7720d8124', 'Azure VM虚拟机状态检测', 1, 'LowRisk', 'Azure  检测您账号下的VM是否停止运行，运行视为“合规”，否则视为“不合规”', 'policies:\n  #  检测您账号下的VM是否停止运行，运行视为“合规”，否则视为“不合规”\n  - name: azure-stopped-vm\n    resource: azure.vm\n    filters:\n     - type: instance-view\n       key: statuses[].code\n       op: not-in\n       value_type: swap\n       value: ${{value}}', '[{\"key\":\"value\",\"name\":\"运行状态\",\"defaultValue\":\"\\\"PowerState/running\\\"\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('94d168d0-8e62-4173-96fd-326eb267273e', 'Azure Disk关联虚拟机检测', 1, 'LowRisk', 'Azure  检测您账号下的Disk是否关联虚拟机，关联视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的Disk是否关联虚拟机，关联视为“合规”，否则视为“不合规”\n  - name: azure-orphaned-disk\n    resource: azure.disk\n    filters:\n      - type: value\n        key: managedBy\n        value: ${{value}}', '[{\"key\":\"value\",\"name\":\"是否关联虚拟机\",\"defaultValue\":\"null\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('c3259122-ac07-4256-83da-b6361c9d3abb', 'Azure SQL Server使用率检测', 1, 'LowRisk', 'Azure  检测您账号下的SQL Server是否x小时内平均DTU消耗少于y％，多于y％视为“合规”，否则视为“不合规”', 'policies:\n  # 检测您账号下的SQL Server是否x小时内平均DTU消耗少于y％，多于y％视为“合规”，否则视为“不合规”\n  - name: azure-dtu-consumption\n    resource: azure.sqlserver\n    filters:\n      - type: metric\n        metric: dtu_consumption_percent\n        aggregation: average\n        op: lt\n        threshold: ${{threshold}}\n        timeframe: ${{timeframe}}\n        filter:  \"DatabaseResourceId eq \'*\'\"', '[{\"key\":\"threshold\",\"name\":\"使用率(%)\",\"defaultValue\":\"10\",\"required\":true},{\"key\":\"timeframe\",\"name\":\"小时(h)\",\"defaultValue\":\"24\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('dd11251a-7fd4-499c-857b-c7dcf4c98958', 'Azure Cosmos DB检测', 1, 'HighRisk', 'Azure  检测所有Cosmos DB帐户没有配置防火墙，或者配置了允许超出预期范围访问的帐户，符合视为“合规”，否则视为“不合规”', 'policies:\n # 检测所有Cosmos DB帐户没有配置防火墙，或者配置了允许超出预期范围访问的帐户，符合视为“合规”，否则视为“不合规”\n - name: azure-cosmos-firewall-enable\n   resource: azure.cosmosdb\n   filters:\n     - or:\n       - type: value\n         key: properties.ipRangeFilter\n         value: empty  # The firewall is disabled\n\n       - not:\n         - type: firewall-rules\n           only:       # Should *only* allow access within the specified maximums here\n             - ${{value1}}\n             - ${{value2}}\n             - ${{value3}}', '[{\"key\":\"value1\",\"name\":\"过滤规则1\",\"defaultValue\":\"19.0.0.0/16\",\"required\":true},{\"key\":\"value2\",\"name\":\"过滤规则2\",\"defaultValue\":\"20.0.1.2\",\"required\":true},{\"key\":\"value3\",\"name\":\"过滤规则3\",\"defaultValue\":\"ServiceTags.AzureCloud\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('f6c72297-33db-49dd-a106-06cf2ff47069', 'Azure 网络接口连接检测', 1, 'LowRisk', 'Azure  检测您账号下的网络接口是否连接到VM，连接视为“合规”，否则视为“不合规”', 'policies:\n  # Azure  检测您账号下的网络接口是否连接到VM，连接视为“合规”，否则视为“不合规”\n  - name: orphaned-nic\n    resource: azure.networkinterface\n    filters:\n      - type: value\n        key: properties.virtualMachine\n        value: ${{value}}', '[{\"key\":\"value\",\"name\":\"是否连接到VM\",\"defaultValue\":\"null\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('f7c03491-fc0a-4ae7-87ed-6423748b2372', 'Azure 存储账户检测', 1, 'HighRisk', 'Azure  检测所有防火墙都配置为仅允许从数据中心（或IP空间的任何块）中的IP地址进行访问，符合视为“合规”，否则视为“不合规”', 'policies:\n  # 检测所有防火墙都配置为仅允许从数据中心（或IP空间的任何块）中的IP地址进行访问，符合视为“合规”，否则视为“不合规”\n  - name: azure-storage-only-allow-datacenter-ips\n    resource: azure.storage\n    filters:\n      - or:\n        - type: value\n          key: properties.networkAcls.defaultAction\n          value: \'Allow\'\n\n        - not:\n          - type: firewall-rules\n            only:\n              - ${{value1}}\n              - ${{value2}}\n              - ${{value3}}', '[{\"key\":\"value1\",\"name\":\"过滤规则1\",\"defaultValue\":\"\'8.8.8.8\'\",\"required\":true},{\"key\":\"value2\",\"name\":\"过滤规则2\",\"defaultValue\":\"\'10.0.0.0/16\'\",\"required\":true},{\"key\":\"value3\",\"name\":\"过滤规则3\",\"defaultValue\":\"\'20.0.0.0 - 20.10.0.0\'\",\"required\":true}]', 'hummer-azure-plugin', 'Microsoft Azure', 'azure.png', concat(unix_timestamp(now()), '005'), 1, 'custodian');
