INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('e28786b3-f2b1-46de-b4b6-7835b42ae58b', 'Aliyun SLB负载均衡监听检测', 1, 'HighRisk', 'Aliyun  账号下的SLB负载均衡是否开启HTTPS或HTTP监听，开启视为合规，否则不合规', 'policies:\n  # 检测您账号下的SLB负载均衡是否开启HTTPS或HTTP监听，开启视为合规，否则不合规。\n  - name: aliyun-slb-listener-type\n    resource: aliyun.slb\n    filters:\n      - type: listener-type\n        value: [${{value1}}, ${{value2}}]', '[{\"key\":\"value1\",\"name\":\"监听类型1\",\"defaultValue\":\"\\\"https\\\"\",\"required\":true},{\"key\":\"value2\",\"name\":\"监听类型2\",\"defaultValue\":\"\\\"http\\\"\",\"required\":true}]', 'hummer-aliyun-plugin', '阿里云', 'aliyun.png', concat(unix_timestamp(now()), '001'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('4d427c93-5645-445a-93a3-0c536d6865ab', 'Aliyun SecurityGroup安全组端口访问检测', 1, 'HighRisk', 'Aliyun  账号下ECS安全组配置允许所有端口访问视为”不合规“，否则为”合规“', 'policies:\n  # 账号下ECS安全组配置允许所有端口访问视为”不合规“，否则为”合规“\n  - name: aliyun-sg-ports\n    resource: aliyun.security-group\n    filters:\n      - type: source-ports\n        SourceCidrIp: ${{SourceCidrIp}}\n        PortRange: ${{PortRange}}', '[{\"key\":\"SourceCidrIp\",\"name\":\"目标IP地址段\",\"defaultValue\":\"\\\"0.0.0.0/0\\\"\",\"required\":true},{\"key\":\"PortRange\",\"name\":\"端口号\",\"defaultValue\":\"”-1/-1“\",\"required\":true}]', 'hummer-aliyun-plugin', '阿里云', 'aliyun.png', concat(unix_timestamp(now()), '001'), 1, 'custodian');
